// DOM Elements
const featuredArticlesContainer = document.getElementById('featuredArticles');
const searchInput = document.getElementById('searchInput');
const searchButton = document.getElementById('searchButton');
const modal = document.getElementById('articleModal');
const closeModal = document.querySelector('.close-modal');
const randomArticleButton = document.getElementById('randomArticle');

// Close modal when clicking the close button or outside the modal
closeModal.addEventListener('click', () => {
    modal.style.display = 'none';
});

window.addEventListener('click', (event) => {
    if (event.target === modal) {
        modal.style.display = 'none';
    }
});

// Random article functionality
randomArticleButton.addEventListener('click', (e) => {
    e.preventDefault();
    const randomIndex = Math.floor(Math.random() * featuredArticles.length);
    viewArticle(featuredArticles[randomIndex]);
});

// Display featured articles
function displayFeaturedArticles() {
    featuredArticlesContainer.innerHTML = '';
    
    featuredArticles.forEach(article => {
        const articleElement = document.createElement('div');
        articleElement.className = 'article-card';
        articleElement.innerHTML = `
            <div class="article-image" style="background-image: url('${article.image}')"></div>
            <div class="article-content">
                <h3>${article.title}</h3>
                <p>${article.excerpt}</p>
                <div class="article-meta">
                    <span>By ${article.author}</span>
                    <span>${article.date}</span>
                </div>
            </div>
        `;
        
        // Add click event to view full article
        articleElement.addEventListener('click', () => {
            viewArticle(article);
        });
        
        featuredArticlesContainer.appendChild(articleElement);
    });
}

// View full article
function viewArticle(article) {
    // Update modal content
    document.getElementById('modalTitle').textContent = article.title;
    document.getElementById('modalAuthor').textContent = `By ${article.author}`;
    document.getElementById('modalDate').textContent = article.date;
    document.getElementById('modalImage').style.backgroundImage = `url('${article.image}')`;
    document.getElementById('modalExcerpt').textContent = article.excerpt;
    
    // Show modal
    modal.style.display = 'block';
}

// Search functionality
function performSearch(query) {
    if (!query.trim()) return;
    
    const results = featuredArticles.filter(article => 
        article.title.toLowerCase().includes(query.toLowerCase()) || 
        article.excerpt.toLowerCase().includes(query.toLowerCase())
    );
    
    if (results.length > 0) {
        featuredArticlesContainer.innerHTML = '';
        results.forEach(article => {
            const articleElement = document.createElement('div');
            articleElement.className = 'article-card';
            articleElement.innerHTML = `
                <div class="article-image" style="background-image: url('${article.image}')"></div>
                <div class="article-content">
                    <h3>${article.title}</h3>
                    <p>${article.excerpt}</p>
                    <div class="article-meta">
                        <span>By ${article.author}</span>
                        <span>${article.date}</span>
                    </div>
                </div>
            `;
            
            articleElement.addEventListener('click', () => {
                viewArticle(article);
            });
            
            featuredArticlesContainer.appendChild(articleElement);
        });
    } else {
        featuredArticlesContainer.innerHTML = '<p class="no-results">No articles found matching your search.</p>';
    }
}

// Event listeners
searchButton.addEventListener('click', () => {
    performSearch(searchInput.value);
});

searchInput.addEventListener('keypress', (e) => {
    if (e.key === 'Enter') {
        performSearch(searchInput.value);
    }
});

// Initialize the page
displayFeaturedArticles();
